<div class="modal-backdrop">
    <div data-notify='container'>
        <div class='modal fade ln-modal show d-block' tabindex='-1' role='dialog'>
            <div class='modal-dialog' role='document'>
                <a  aria-hidden='true' class='ln-notify-close' data-dismiss='modal' data-notify='dismiss'><i class='sl sl-icon-close'></i></a>
                <div class="modal-header ln-modal-header" data-notify='title'>
                    <h4 class="p-0 text u-text-bold">{1}</h4>
                </div>
                <div class='modal-body ln-modal-body'>
                    <form action="">
                        <div class="row justify-content-center">
                            <div class="col-md-10">

                                <div class="c-field">
                                    <label for="password" class="c-field__label">Enter password</label>
                                    <input type="password" name="password" id="password" class="c-input">
                                </div>
                                <div class="c-field mt-4 clearfix text-center">
                                    <button class="btn btn-block btn-primary">Login</button>
                                </div>
                            </div>
                        </div>
                    </form>
                </div>
                <div class='progress' data-notify='progressbar'>
                    <div class='progress-bar progress-bar-striped progress-bar-animated bg-{0}' role='progressbar' aria-valuenow='0' aria-valuemin='0' aria-valuemax='100' style='width: 0%;'></div>
                </div>
            </div>
        </div>
    </div>
</div>